mapper.select:
select wi.id as id, wi.in_name as inName, wi.in_type as inType, wi.in_specifications as inSpecifications, wi.in_cost as inCost, wi.in_qty as inQty, wi.in_pricing as inPricing, wi.in_manufacturers as inManufacturers, wp.parts_name as inPart, date_format(wi.in_updatetime,'%Y-%c-%d %H:%i:%S') AS inUpdatetime, date_format(wi.in_createtime,'%Y-%c-%d %H:%i:%S') AS inCreatetime from w_instock wi left join w_parts wp on wp.id=wi.in_part 1=1
AND wi.in_name like CONCAT('%',#{inName},'%')
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')>=#{inTime1}
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')<=#{inTime2}
AND wi.in_type='主乐器'
group by id

select wi.id as id, wi.in_name as inName, wi.in_type as inType, wi.in_specifications as inSpecifications, wi.in_cost as inCost, wi.in_qty as inQty, wi.in_pricing as inPricing, wi.in_manufacturers as inManufacturers, wp.parts_name as inPart, date_format(wi.in_updatetime,'%Y-%c-%d %H:%i:%S') AS inUpdatetime, date_format(wi.in_createtime,'%Y-%c-%d %H:%i:%S') AS inCreatetime from w_instock wi left join w_parts wp on wp.id=wi.in_part 1=1
AND wi.in_name like CONCAT('%',#{inName},'%')
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')>=#{inTime1}
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')<=#{inTime2}
AND wi.in_type='配件'
group by id

mapper.delete:
 delete from w_instock where id in ( 
 
    #{id}
)
