mapper.select:
select wii.id as id, wii.ins_name as insName, wii.ins_type as insType, wii.ins_specifications as insSpecifications, wii.ins_cost as insCost, wii.ins_pricing as insPricing, wii.ins_manufacturers as insManufacturers, wp.parts_name as insParts, wii.ins_remake as insRemake, date_format(wii.ins_time,'%Y-%c-%d %h:%i:%s') AS insTime from w_instrument_information wii left join w_parts wp on wp.id=wii.ins_parts 1=1
AND wii.ins_name like CONCAT('%',#{insName},'%')
AND DATE_FORMAT(wii.ins_time , '%Y-%m-%d')>=#{insTime1}
AND DATE_FORMAT(wii.ins_time , '%Y-%m-%d')<=#{insTime2}
group by id

mapper.delete:
 delete from w_instrument_information where id in ( 
 
    #{id}
)
