mapper.select:
select wi.id as id, wi.in_name as inName, wi.in_type as inType, wi.in_specifications as inSpecifications, wi.in_cost as inCost, wi.in_qty as inQty, wi.in_pricing as inPricing, wi.in_manufacturers as inManufacturers, date_format(wi.in_createtime,'%Y-%c-%d %H:%i:%S') AS inCreatetime from w_instock wi 1=1
AND wi.in_name like CONCAT('%',#{inName},'%')
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')>=#{inTime1}
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')<=#{inTime2}
group by id

select wi.id as id, wi.out_name as outName, wi.out_type as outType, wi.out_specifications as outSpecifications, wi.out_cost as outCost, wi.out_pricing as outPricing, wi.out_qty as outQty, wi.out_profit as outProfit, wi.out_vip as outVip, wi.out_manufacturers as outManufacturers, date_format(wi.out_createtime,'%Y-%c-%d %H:%i:%S') AS outCreatetime from w_outstock wi 1=1
AND wi.out_name like CONCAT('%',#{inName},'%')
AND DATE_FORMAT(wi.out_createtime , '%Y-%m-%d')>=#{inTime1}
AND DATE_FORMAT(wi.out_createtime , '%Y-%m-%d')<=#{inTime2}
group by id

select wi.id as id, wi.in_name as inName, wi.in_type as inType, wi.in_specifications as inSpecifications, wi.in_cost as inCost, wi.in_pricing as inPricing, CONCAT('入库:',wi.in_qty) as inQty, wi.in_manufacturers as inManufacturers, '入库' as instock, date_format(wi.in_createtime,'%Y-%c-%d %H:%i:%S') AS inCreatetime from w_instock wi 1=1
AND wi.in_name like CONCAT('%',#{insName},'%')
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')>=#{insTime1}
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')<=#{insTime2}
UNION ALL select wo.id as id, wo.out_name as inName, wo.out_type as inType, wo.out_specifications as inSpecifications, wo.out_cost as inCost, wo.out_pricing as inPricing, CONCAT('出售:',wo.out_qty) as inQty, wo.out_manufacturers as inManufacturers, '出库' as instock, date_format(wo.out_createtime,'%Y-%c-%d %H:%i:%S') AS inCreatetime from w_outstock wo 1=1
AND wo.out_name like CONCAT('%',#{insName},'%')
AND DATE_FORMAT(wo.out_createtime , '%Y-%m-%d')>=#{insTime1}
AND DATE_FORMAT(wo.out_createtime , '%Y-%m-%d')<=#{insTime2}

select wi.id as id, '支出' as inKind, wi.in_name as inName, wi.in_type as inType, wi.in_specifications as inSpecifications, CONCAT('入库:',wi.in_qty) as inQty, wi.in_qty*wi.in_cost as inProfit, wi.in_manufacturers as inManufacturers, date_format(wi.in_createtime,'%Y-%c-%d %H:%i:%S') AS inCreatetime from w_instock wi 1=1
AND wi.in_name like CONCAT('%',#{insName},'%')
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')>=#{insTime1}
AND DATE_FORMAT(wi.in_createtime , '%Y-%m-%d')<=#{insTime2}
UNION ALL select wo.id as id, '收入' as inKind, wo.out_name as inName, wo.out_type as inType, wo.out_specifications as inSpecifications, CONCAT('出库:',wo.out_qty) as inQty, wo.out_qty*wo.out_cost as inProfit, wo.out_manufacturers as inManufacturers, date_format(wo.out_createtime,'%Y-%c-%d %H:%i:%S') AS inCreatetime from w_outstock wo 1=1
AND wo.out_name like CONCAT('%',#{insName},'%')
AND DATE_FORMAT(wo.out_createtime , '%Y-%m-%d')>=#{insTime1}
AND DATE_FORMAT(wo.out_createtime , '%Y-%m-%d')<=#{insTime2}
