mapper.select:
select id as id, vip_name as vipName, vip_gender as vipGender, vip_age as vipAge, vip_phone as vipPhone, vip_integral as vipIntegral, date_format(vip_createtime,'%Y-%c-%d %H:%i:%S') AS vipCreatetime, date_format(vip_updatetime,'%Y-%c-%d %H:%i:%S') AS vipUpdatetime from w_vip 1=1
AND vip_name like CONCAT('%',#{vipName},'%')
AND DATE_FORMAT(vip_createtime ,'%Y-%c-%d')>=#{vipTime1}
AND DATE_FORMAT(vip_createtime ,'%Y-%c-%d')<=#{vipTime2}
group by id

mapper.delete:
 delete from w_vip where id in ( 
 
    #{id}
)
