mapper.select:
select id as id, parts_name as insName, parts_type as insType, parts_specifications as insSpecifications, parts_cost as insCost, parts_pricing as insPricing, parts_manufacturers as insManufacturers, parts_remake as insRemake, date_format(parts_time,'%Y-%c-%d %h:%i:%s') AS insTime from w_parts 1=1
AND parts_name like CONCAT('%',#{insName},'%')
AND DATE_FORMAT(parts_time , '%Y-%m-%d')>=#{insTime1}
AND DATE_FORMAT(parts_time , '%Y-%m-%d')<=#{insTime2}
group by id

mapper.delete:
 delete from w_parts where id in ( 
 
    #{id}
)
