mapper.select:
select wp.id as id, wp.parts_name as insName, wii.ins_name as insType, wp.parts_specifications as insSpecifications, wp.parts_cost as insCost, wp.parts_pricing as insPricing, wp.parts_manufacturers as insManufacturers, wp.parts_remake as insRemake, date_format(wp.parts_time,'%Y-%c-%d %h:%i:%s') AS insTime from w_parts wp left join w_instrument_information wii on wii.id=wp.parts_type 1=1
AND wp.parts_name like CONCAT('%',#{insName},'%')
AND DATE_FORMAT(wp.parts_time , '%Y-%m-%d')>=#{insTime1}
AND DATE_FORMAT(wp.parts_time , '%Y-%m-%d')<=#{insTime2}
group by id

mapper.delete:
 delete from w_parts where id in ( 
 
    #{id}
)
